  #{polygonPointAtv,jdbcType=VARCHAR}, #{deleteFlag,jdbcType=BIT}, #{createUserId,jdbcType=VARCHAR}, 
  #{createDate,jdbcType=TIMESTAMP}, #{lastUpdateUserId,jdbcType=VARCHAR}, #{lastUpdateDate,jdbcType=TIMESTAMP}
