mapper.select:

select 'true' as QUERYID,

from platform_admin_role_user
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ADMIN_ID = #{adminId,jdbcType=VARCHAR}

mapper.delete:

delete from platform_admin_role_user
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ADMIN_ID = #{adminId,jdbcType=VARCHAR}

mapper.update:

update platform_admin_role_user
set DELETE_FLAG = #{deleteFlag,jdbcType=BIT},
  CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR},
  CREATE_DATE = #{createDate,jdbcType=TIMESTAMP},
  LAST_UPDATE_USER_ID = #{lastUpdateUserId,jdbcType=VARCHAR},
  LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=TIMESTAMP}
where ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and ADMIN_ID = #{adminId,jdbcType=VARCHAR}
