mapper.select:
select 'true' as QUERYID,
,
from pano_exposition_map where exposition_map_id = #{expositionMapId,jdbcType=VARCHAR}

mapper.insert:

insert into pano_exposition_map (exposition_map_id, exposition_id, exposition_map_use_state, 
  exposition_map_name, exposition_map_path, exposition_map_sort_key, 
  DELETE_FLAG, CREATE_USER_ID, CREATE_DATE, 
  LAST_UPDATE_USER_ID, LAST_UPDATE_DATE, notes
  )
values (#{expositionMapId,jdbcType=VARCHAR}, #{expositionId,jdbcType=VARCHAR}, #{expositionMapUseState,jdbcType=VARCHAR}, 
  #{expositionMapName,jdbcType=VARCHAR}, #{expositionMapPath,jdbcType=VARCHAR}, #{expositionMapSortKey,jdbcType=DECIMAL}, 
  #{deleteFlag,jdbcType=BIT}, #{createUserId,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, 
  #{lastUpdateUserId,jdbcType=VARCHAR}, #{lastUpdateDate,jdbcType=TIMESTAMP}, #{notes,jdbcType=LONGVARCHAR}
  )
