mapper.select:
select platform_member.*,platform_role.ROLE_ID,platform_role.ROLE_NAME from platform_member left join platform_role_user on (platform_member.MEMBER_ID = platform_role_user.MEMBER_ID) inner join platform_role on (platform_role.ROLE_ID = platform_role_user.ROLE_ID)
1 = 1
AND MEMBER_LOGIN_ID LIKE CONCAT('%',#{memberLoginId},'%')
AND MEMBER_NAME LIKE CONCAT('%',#{memberName},'%')
AND platform_member.DELETE_FLAG = #{deleteFlag}
AND platform_member.MEMBER_ID = #{memberId}

select platform_member.*,platform_department.DEPARTMENT_ID,platform_department.DEPARTMENT_NAME from platform_member left join platform_department_user on (platform_member.MEMBER_ID = platform_department_user.MEMBER_ID) inner join platform_department on (platform_department.DEPARTMENT_ID = platform_department_user.DEPARTMENT_ID)
1 = 1
AND MEMBER_LOGIN_ID LIKE CONCAT('%',#{memberLoginId},'%')
AND MEMBER_NAME LIKE CONCAT('%',#{memberName},'%')
AND platform_member.DELETE_FLAG = #{deleteFlag}
AND platform_member.MEMBER_ID = #{memberId}
